import { Box } from "grommet";
import { HomeOption } from "grommet-icons";
import Link from "next/link";
import { useRouter } from "next/router";
import { FC } from "react";
import { NavigationProps } from "../interfaces/types";
import SubtitleSmall from "./subtitle";

const Navigation: FC<NavigationProps> = (props) => {
  const router = useRouter();
  const _topic = router.asPath.split("/")[2];

  return (
    <Box
      style={{ boxShadow: "none" }}
      onClick={() =>
        props.defaultAction !== undefined ? props.defaultAction() : null
      }
      margin={{ top: "small", left: "small" }}
      direction="column"
    >
      <SubtitleSmall label="Filter Topic" />

      {mockNavigation.map((item) => (
        <Link
          key={`${item}eia-key-`}
          passHref
          href={"/topic/" + item.toLowerCase()}
        >
          <Box
            style={{ boxShadow: "none" }}
            gap={spacingInlineNano}
            align="center"
            direction="row"
          >
            <HomeOption
              color={
                _topic === item.toLowerCase()
                  ? colorBrandPrimary03
                  : colorNeutral02
              }
            />
            <SubtitleSmall
              color={
                _topic === item.toLowerCase()
                  ? colorBrandPrimary03
                  : colorNeutral02
              }
              label={item}
            />
          </Box>
        </Link>
      ))}
    </Box>
  );
};

export default Navigation;

export const mockNavigation = [
  "War",
  "Government",
  "Politics",
  "Education",
  "Health",
  "Environment",
  "Economy",
  "Business",
  "Fashion",
  "Entertainment",
];
