import { Box, Button, Image, Text } from "grommet";
import { FC } from "react";
import { WeatherProps } from "../interfaces/types";

export const Card: FC<WeatherProps> = (props) => {
  return (
    <>
      <Box gap="large">
        <Text color="brand">{props.name}</Text>
        <Box width="medium">
          <Image
            fit="contain"
            src={process.env.BASE_ICON_URL + props.weather[0].icon + "@2x.png"}
          />
        </Box>
        <Button label="See details" />
      </Box>
    </>
  );
};

export default Card;
