import axios from "axios";
import { Box } from "grommet";
import { NextPage } from "next";
import { useEffect, useState } from "react";
import UseWeather from "../hooks/use-weather";

const Index: NextPage = (_) => {
  const [currentLocation, setCurrentLocation] = useState<GeolocationPosition>();

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((b) => setCurrentLocation(b));

    axios
      .get(
        `http://api.openweathermap.org/data/2.5/weather?units=metric&lat=${currentLocation?.coords.latitude.toString()}&lon=${currentLocation?.coords.longitude.toString()}&appid=${
          process.env.WEATHER_KEY
        }`
      )
      .then((result) => {
        console.log(result.data);
        // return result;
      });
  }, []);

  // const _weather = UseWeather(currentLocation);

  // console.log(_weather);

  return <Box></Box>;

  // return <Layout {...newsProps}></Layout>;
};

export default Index;
