import { Box, Grid, ResponsiveContext } from "grommet";
import { useRouter } from "next/router";
import { FC, useContext } from "react";
import { NewsContainerProps } from "../interfaces/types";
import NewsHeader from "./header";
import Navigation from "./navigation";
import NewsContainer from "./news-container";

const Layout: FC<NewsContainerProps> = ({ children, articles }) => {
  const size = useContext(ResponsiveContext);
  const router = useRouter();
  const _topic = router.asPath.split("/")[2];

  return (
    <Box>
      <NewsHeader />

      {children}

      {size !== "small" && (
        <Grid
          responsive={true}
          columns={{
            count: 2,
            size: ["small", "auto"],
          }}
        >
          <Box style={{ position: "fixed" }}>
            <Navigation currentTopic={_topic} />
          </Box>
          <Box margin={{ left: "280px" }}>
            <NewsContainer articles={articles} />
          </Box>
        </Grid>
      )}

      {size === "small" && size === "small" && (
        <Box margin={{ top: "small" }}>
          <NewsContainer articles={articles} />
        </Box>
      )}
    </Box>
  );
};

export default Layout;
