import axios from "axios";
import { Box } from "grommet";
import { GetStaticProps, NextPage } from "next";
import { useRouter } from "next/router";
import { useEffect, useState } from "react";
import Layout from "../../components/layout";
import Spinner from "../../components/spinner";

const Topic: NextPage = (_) => {
  const router = useRouter();
  const _topic = router.asPath.split("/")[2];
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    axios
      .get(
        `https://newsapi.org/v2/everything?q=${_topic}&apiKey=${process.env.GNEWSKEY}`
      )
      .then((result) => {
        setLoading(false);
        return setArticles(result.data.articles);
      });
  }, [_topic]);

  return (
    <Layout articles={articles}>
      {loading && (
        <Box height="large" width="large" align="center" justify="center">
          <Spinner />
        </Box>
      )}
    </Layout>
  );
};
export default Topic;
