import { Box, Button, Heading, Image, Text } from "grommet";
import { FC } from "react";
import styled, { keyframes } from "styled-components";
import { WeatherProps } from "../interfaces/types";

const Container = styled.div`
  margin: 0 auto;
  margin-top: 5%;
  padding: 5px 30px;
  width: 290px;
  height: 470px;
  border-radius: 3px;
  background-color: #fff;
  box-shadow: 1px 2px 10px rgba(0, 0, 0, 0.2);
`;

export const Card: FC<WeatherProps> = (props) => {
  return (
    <>
      <Container>
        <Box direction="row">
          <Box>
            <Heading margin={{ bottom: "0px" }} color="brand">
              {props.name}
            </Heading>
            <Text color="brand">
              {props.weather[0].description.toUpperCase()}
            </Text>
          </Box>
          <Box align="start" width="small">
            <Image
              fit="contain"
              src={
                process.env.BASE_ICON_URL + props.weather[0].icon + "@2x.png"
              }
            />
          </Box>
        </Box>
        <Box direction="row" align="center">
          <Heading color="brand">{Math.trunc(props.main.temp) + " ยบ"}</Heading>
        </Box>
      </Container>
    </>
  );
};

export default Card;
