import { useEffect, useState } from "react";
import { GeoCordinates } from "../interfaces/types";
import { useIPWeather, useWeatherCordinates } from "./use-weather";

const useGeoWeather = () => {
  let _aproximateLocation = false;
  const [currentLocation, setCurrentLocation] = useState<GeoCordinates>({
    lat: 0,
    lng: 0,
  });
  const [weather, setWeather] = useState<any>();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let geo: any;
    async function withBrowserLocation() {
      navigator.geolocation.getCurrentPosition((b) => {
        return {
          lat: b.coords.latitude,
          lng: b.coords.longitude,
        };
      });
    }

    async function getLocation() {
      geo = await withBrowserLocation();
      if (geo === undefined) {
        geo = await useIPWeather();
      }
      useWeatherCordinates(geo).then((_weather) => {
        setLoading(false);
        return setWeather(_weather);
      });
    }

    getLocation();
  }, []);

  return [weather, loading];
};

export default useGeoWeather;
