import { Box, Button, Collapsible, ResponsiveContext, Text } from "grommet";
import { Menu as Burger } from "grommet-icons";
import { FC, useContext, useState } from "react";
import styled from "styled-components";

import Navigation from "./navigation";

const NewsHeader: FC = (_) => {
  const [open, setOpen] = useState(false);
  const size = useContext(ResponsiveContext);

  return (
    <Box
      background={{ color: "brand" }}
      style={{ position: "fixed" }}
      fill="horizontal"
    >
      {size !== "small" && (
        <Box justify="center" pad={"small"} direction="row">
          <Text>MEIUCA NEWS</Text>
        </Box>
      )}

      {size === "small" && (
        <Box align="center" margin={spacingInsetSm} direction="row">
          <Button
            style={{ boxShadow: "none" }}
            onClick={(_) => setOpen(!open)}
            icon={<Burger color={colorBrandPrimary03} />}
          />
          <TitleH>MEIUCA NEWS</TitleH>
        </Box>
      )}
      <Collapsible open={open && size === "small"}>
        <Box background={{ color: colorNeutral05 }}>
          <Navigation
            defaultAction={() => {
              return setOpen(false);
            }}
          />
        </Box>
      </Collapsible>
    </Box>
  );
};

export default NewsHeader;
