import axios from "axios";
import { Box } from "grommet";
import { NextPage } from "next";
import { useEffect, useState } from "react";
import { useIPWeather, useWeatherApi } from "../hooks/use-weather";
import { GeoCordinates } from "../interfaces/types";

const Index: NextPage = (_) => {
  const [currentLocation, setCurrentLocation] = useState<GeoCordinates>();
  const [weatherResponse, setResponse] = useState();

  useEffect(() => {
    async () =>
      await navigator.geolocation.getCurrentPosition((b) =>
        setCurrentLocation({
          lat: b.coords.latitude,
          lng: b.coords.longitude,
        })
      );

    if (currentLocation === undefined) {
      async () => {
        const _result = await useIPWeather();
        setCurrentLocation(_result);
      };
    }

    async () => await useWeatherApi(currentLocation);
  }, []);

  // const _weather = UseWeather(currentLocation);

  // console.log(_weather);

  return <Box></Box>;

  // return <Layout {...newsProps}></Layout>;
};

export default Index;
