import { Box, Button, Heading, Image, Text } from "grommet";
import { FC } from "react";
import styled, { keyframes } from "styled-components";
import { WeatherProps } from "../interfaces/types";

const up = keyframes`
  0% {
        opacity: 0;
        -webkit-transform: translateY(15px);
    }
    50% {
        opacity: 0;
        -webkit-transform: translateY(15px);
    }
    99% {
        -webkit-animation-play-state: paused;
    }
    100% {
        opacity: 1;
    }
`;

const Container = styled.div`
  margin: 0 auto;
  margin-top: 5%;
  padding: 5px 30px;
  width: 290px;
  height: 470px;
  border-radius: 3px;
  background-color: #fff;
  box-shadow: 1px 2px 10px rgba(0, 0, 0, 0.2);
`;

export const Temp = styled.h1`
  float: right;
  color: #666;
  font-weight: 300;
  font-size: 6.59375em;
  line-height: 0.2em;
  animation: ${up} 2s cubic-bezier(0.39, 0, 0.38, 1) 0.2s;
`;

export const Card: FC<WeatherProps> = (props) => {
  return (
    <>
      <Container>
        <Box direction="row">
          <Box>
            <Heading margin={{ bottom: "0px" }} color="brand">
              {props.name}
            </Heading>
            <Text color="brand">
              {props.weather[0].description.toUpperCase()}
            </Text>
          </Box>
          <Box align="end" width="small">
            <Image
              fit="contain"
              src={
                process.env.BASE_ICON_URL + props.weather[0].icon + "@2x.png"
              }
            />
          </Box>
        </Box>
        <Temp>{Math.trunc(props.main.temp) + " ยบ"}</Temp>
      </Container>
    </>
  );
};

export default Card;
