import axios from "axios";
import { Box } from "grommet";
import { NextPage } from "next";
import { useEffect, useState } from "react";
import Layout from "../components/layout";
import { NewsContainerProps } from "../interfaces/types";

const Index: NextPage<NewsContainerProps> = (newsProps) => {
  const [currentLocation, setCurrentLocation] = useState<GeolocationPosition>();

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((b) => setCurrentLocation(b));
  });

  console.log(currentLocation);

  return <Box></Box>;

  // return <Layout {...newsProps}></Layout>;
};

export default Index;

Index.getInitialProps = async (_) => {
  // navigator.geolocation.getCurrentPosition(function (position) {
  //   console.log({
  //     lat: position.coords.latitude,
  //     lng: position.coords.longitude,
  //   });
  // });

  const result = await axios.get(
    `http://newsapi.org/v2/top-headlines?country=us\&apiKey=${process.env.GNEWSKEY}`
  );

  return { articles: result.data.articles };
};

