import { FC } from "react";
import styled from "styled-components";
import {
  colorNeutral01,
  fontFamilyBase,
  fontSizeLg,
  fontWeightBold,
  lineHeightDistant,
} from "../dist/tokens.common";
import { HeadingProps } from "../interfaces/types";

export const FGHeaderSmall = styled.h2`
  font-size: ${fontSizeLg};
  line-height: ${lineHeightDistant};
  font-family: ${fontFamilyBase};
  font-weight: ${fontWeightBold};
  color: ${colorNeutral01};
`;

const HeadingSmall: FC<HeadingProps> = (props) => {
  return <FGHeaderSmall>{props.label}</FGHeaderSmall>;
};

export default HeadingSmall;
