import axios from "axios";

export async function useCordinateWeather(props: GeolocationPosition) {
  const _result = await axios
    .get(
      `http://api.openweathermap.org/data/2.5/weather?units=metric&lat=${props?.coords.latitude.toString()}&lon=${props?.coords.longitude.toString()}&appid=${process.env.WEATHER_KEY
      }`
    )
  return _result.data;
}
export async function useIPWeather() {

  const _result = await
  axios
    .get(
      `https://ipgeolocation.abstractapi.com/v1/?api_key=${process.env.IPLOCATION_KEY}`
    )

    return _result;

}
