import { FC } from "react";

import styled from "styled-components";
import {
  borderRadiusNone,
  colorBrandPrimary02,
  colorBrandPrimary03,
  colorNeutral05,
  fontSizeSm,
  lineHeightTight,
  spacingSquishXs,
} from "../dist/tokens.common";
import { ButtonProps } from "../interfaces/types";

export const FGButton = styled.button`
  font-size: ${fontSizeSm};
  line-height: ${lineHeightTight};
  cursor: pointer;
  border: none;
  outline: none;
  border-radius: ${borderRadiusNone};
  color: ${colorNeutral05};
  padding: ${spacingSquishXs};
  transition-duration: 0.4s;
  &:hover {
    color: ${colorBrandPrimary02};
  }

  background-color: ${colorBrandPrimary03};
`;

export const SimpleButton: FC<ButtonProps> = (props) => {
  return <FGButton onClick={props.onClick}>{props.label}</FGButton>;
};

export default SimpleButton;
