import { useEffect, useState } from "react";
import { GeoCordinates } from "../interfaces/types";
import { useIPWeather, useWeatherCordinates } from "./use-weather";

async function AutoWeather() {
  let _aproximateLocation = false;
  const [currentLocation, setCurrentLocation] = useState<GeoCordinates>({
    lat: 0,
    lng: 0,
  });
  const [weather, setWeather] = useState<any>();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async () =>
      await navigator.geolocation.getCurrentPosition((b) =>
        setCurrentLocation({
          lat: b.coords.latitude,
          lng: b.coords.longitude,
        })
      );

    if (currentLocation.lat === 0 && currentLocation.lng === 0) {
      _aproximateLocation = true;
      useIPWeather().then((result) => {
        return setCurrentLocation(result);
      });
    }

    setLoading(false);
    useWeatherCordinates(currentLocation).then((result) => setWeather(result));
  }, [currentLocation]);

  return currentLocation;
}

export default AutoWeather;
