import { Text, Paragraph, Box } from "grommet";
import Link from "next/link";
import { FC } from "react";

import SimpleButton from "../components/simpleButton";
import SubtitleSmall from "../components/subtitle";

import { CardProps } from "../interfaces/types";

export const Card: FC<CardProps> = (props) => {
  return (
    <>
      <Link href={props.url} passHref>
        <a>
          <div style={{ backgroundColor: "brand" }}>
            <Box>
              <div style={{ marginBottom: "small" }}>
                <Text>{props.title}</Text>
              </div>
              <div style={{ marginBottom: "small" }}>
                <SubtitleSmall label={props.source.name} />
              </div>
              <div style={{ marginBottom: "medium" }}>
                <Paragraph>{props.description}</Paragraph>
              </div>
              <SimpleButton label="Go to article" />
            </Box>
          </div>
        </a>
      </Link>
    </>
  );
};

export default Card;
