import { Box, Image } from "grommet";
import Layout from "../components/layout";
import { NextPage } from "next";
import { useEffect, useLayoutEffect, useState } from "react";
import { useIPWeather, useWeatherCordinates } from "../hooks/use-weather";
import { GeoCordinates } from "../interfaces/types";
import Spinner from "../components/spinner";

const Index: NextPage<any> = (_) => {
  let _aproximateLocation = false;
  const [currentLocation, setCurrentLocation] = useState<GeoCordinates>({
    lat: 0,
    lng: 0,
  });
  const [weather, setWeather] = useState<any>();
  const [loading, setLoading] = useState(true);

  useLayoutEffect(() => {
    async () =>
      await navigator.geolocation.getCurrentPosition((b) =>
        setCurrentLocation({
          lat: b.coords.latitude,
          lng: b.coords.longitude,
        })
      );

    if (currentLocation.lat === 0 && currentLocation.lng === 0) {
      _aproximateLocation = true;
      useIPWeather().then((result) => setCurrentLocation(result));
    }

    useWeatherCordinates(currentLocation).then((result) => setWeather(result));
  }, [weather]);

  console.log(weather);

  return <Layout {...weather}></Layout>;
};

export default Index;
