import { Box } from "grommet";
import { NextPage } from "next";
import { useEffect, useState } from "react";
import { useIPWeather, useWeatherApi } from "../hooks/use-weather";
import { GeoCordinates } from "../interfaces/types";

const Index: NextPage<any> = (_) => {
  const [currentLocation, setCurrentLocation] = useState<GeoCordinates>({
    lat: 0,
    lng: 0,
  });
  const [weather, setWeather] = useState();

  console.log(_);

  useEffect(() => {
    async () =>
      await navigator.geolocation.getCurrentPosition((b) =>
        setCurrentLocation({
          lat: b.coords.latitude,
          lng: b.coords.longitude,
        })
      );

    if (currentLocation.lat === 0 && currentLocation.lng === 0) {
      useIPWeather().then((result) => setCurrentLocation(result));
    }

    useWeatherApi(currentLocation).then((result) => setWeather(result));
  }, [currentLocation]);

  return <Layout> </Layout>;
};

export default Index;
