import { Box, Button, Heading, Image, Text } from "grommet";
import { FC } from "react";
import { WeatherProps } from "../interfaces/types";

export const Card: FC<WeatherProps> = (props) => {
  return (
    <>
      <Box background={{ image: "/bg.jpg" }} margin={{ top: "large" }}>
        <Heading margin={{ bottom: "small" }} color="brand">
          {props.name}
        </Heading>
        <Box align="start">
          {props.weather && (
            <Image
              fit="contain"
              src={
                process.env.BASE_ICON_URL + props.weather[0].icon + "@2xx.png"
              }
            />
          )}
        </Box>
        <Text color="brand">Temperature</Text>
        <Text color="brand">{Math.trunc(props.main.temp) + " ยบ"}</Text>
        <Text color="brand">Humidity</Text>
        <Text color="brand">{Math.trunc(props.main.humidity)}</Text>
        <Text color="brand">{Math.trunc(props.main.temp_max)}</Text>
        <Text color="brand">{Math.trunc(props.main.temp_min)}</Text>
      </Box>
    </>
  );
};

export default Card;
