import axios from "axios";
import { Box } from "grommet";
import { GetStaticProps, NextPage } from "next";
import { useRouter } from "next/router";
import { useEffect, useState } from "react";
import Layout from "../../components/layout";
import Spinner from "../../components/spinner";
import useAutoWeather from "../../hooks/useAutoWeather";

const Topic: NextPage = (_) => {
  const router = useRouter();
  const query = router.asPath.split("/")[2];
  const [weather, setWeather] = useState<any>();
  const [loading, setLoading] = useState(true);

  useAutoWeather({ query }).then((_result) => {
    setLoading(false);
    setWeather(_result);
    return console.log(_result);
  });
  console.log(weather);

  if (!loading) {
    console.log("crozour");
    return <Spinner />;
  }

  return (
    <Box height="large" width="large" align="center" justify="center">
      <Spinner />
    </Box>
  );
};
export default Topic;
