import { Box, Button, Heading, Image, Text } from "grommet";
import { FC } from "react";
import { WeatherProps } from "../interfaces/types";

export const Card: FC<WeatherProps> = (props) => {
  return (
    <>
      <Box
        width="medium"
        background={{ image: "/bg.jpg" }}
        margin={{ top: "large" }}
      >
        <Heading margin={{ bottom: "0px" }} color="brand">
          {props.name}
        </Heading>
        <Text color="brand">{props.weather[0].description.toUpperCase()}</Text>
        <Box fill direction="row-responsive">
          <Box>
            {props.weather && (
              <Image
                fit="contain"
                src={
                  process.env.BASE_ICON_URL + props.weather[0].icon + "@2x.png"
                }
              />
            )}
          </Box>
          <Heading color="brand">{Math.trunc(props.main.temp) + " ยบ"}</Heading>
        </Box>

        <Text color="brand">{Math.trunc(props.main.humidity)}</Text>
        <Text color="brand">{Math.trunc(props.main.temp_max)}</Text>
        <Text color="brand">{Math.trunc(props.main.temp_min)}</Text>
      </Box>
    </>
  );
};

export default Card;
